import './App.css';
import { useState } from "react";
import Axios from 'axios';

function App() {
  const [nome, setNome] = useState("");
  const [rg, setRg] = useState("");
  const [cpf, setCpf] = useState("");
  const [nascimento, setNascimento] = useState(new Date());
  const [admissao, setAdmissao] = useState(new Date());
  const [funcao, setFuncao] = useState("");

  const [listaPessoas, setLista] = useState([]);

  const salvarDados = () => {
    Axios.post("http://localhost:3001/create", {
      nome: nome,
      rg: rg,
      cpf: cpf,
      nascimento: nascimento,
      admissao: admissao,
      funcao: funcao
    }).then(() => {
      console.log("[+] Postagem dos dados bem sucedida");
    });
  };

  const consultarDados = () => {
    Axios.get("http://localhost:3001/pessoas").then((response) => {
      console.log("[+] Consulta aos banco de dados efetuada com sucesso");
    });
  };

  return (
    <div className="App">
      <div className="form">
        <button onClick={consultarDados}>Exibir dados</button>
      </div>
      <div className="form">
        <p>Formulário:</p>
        <label>Nome: </label>
        <input type="text" maxlength="100"
          onChange={(event) => { setNome(event.target.value); }} />
        <label>  RG: </label>
        <input type="text" maxlength="100"
          onChange={(event) => { setRg(event.target.value); }} />
        <label>  CPF: </label>
        <input type="text" maxlength="100"
          onChange={(event) => { setCpf(event.target.value); }} />
        <label>  Data de Nascimento: </label>
        <input type="date"
          onChange={(event) => { setNascimento(event.target.value); }} />
        <label>  Data de Admissão: </label>
        <input type="date"
          onChange={(event) => { setAdmissao(event.target.value); }} />
        <label>  Função: </label>
        <input type="text" maxlength="100"
          onChange={(event) => { setFuncao(event.target.value); }} />
        <button onClick={salvarDados}>Adicionar</button>
      </div>
    </div>
  );
}

export default App;
